// GetSyscallName returns the name for a syscall number
func GetSyscallName(nr uint64) string {
	// Simple mapping for common syscalls
	// In a real implementation, this would be auto-generated from system headers
	
	// Delegate to architecture specific mapping
	switch runtime.GOARCH {
	case "amd64":
		return getSyscallNameAMD64(nr)
	case "arm64":
		return getSyscallNameARM64(nr)
	default:
		return "unknown"
	}
}

